import React, { useState } from 'react';

function BusSearch() {
  const [source, setSource] = useState('');
  const [destination, setDestination] = useState('');
  const [journeyDate, setJourneyDate] = useState('');
  const [busData, setBusData] = useState(null);

  const handleSearch = async () => {
    if (!source || !destination || !journeyDate) {
      alert('Please fill in all fields');
      return;
    }

    try {
      const response = await fetch(`/get_buses?source=${source}&destination=${destination}&jdate=${journeyDate}`);
      const data = await response.json();
      setBusData(data);
    } catch (error) {
      alert('Error fetching bus data');
    }
  };

  return (
    <div>
      <div>
        <input
          type="text"
          placeholder="From (e.g., Bangalore)"
          value={source}
          onChange={(e) => setSource(e.target.value)}
        />
        <input
          type="text"
          placeholder="To (e.g., Ananthapur)"
          value={destination}
          onChange={(e) => setDestination(e.target.value)}
        />
        <input
          type="date"
          value={journeyDate}
          onChange={(e) => setJourneyDate(e.target.value)}
        />
        <button onClick={handleSearch}>Search</button>
      </div>

      {busData && busData.serviceDetailsList ? (
        <div>
          {busData.serviceDetailsList.map((bus) => (
            <div key={bus.serviceNumber}>
              <h3>{bus.travelerAgentName}</h3>
              <p>Bus Type: {bus.busTypeName}</p>
              <p>Fare: â‚¹{bus.fare}</p>
              <p>Seats Available: {bus.availableSeats}</p>
            </div>
          ))}
        </div>
      ) : (
        busData && <p>No buses found</p>
      )}
    </div>
  );
}

export default BusSearch;
