// App.js
import React, { useState, useEffect } from "react";
import $ from "jquery";
import { BrowserRouter as Router, Route, Routes, useNavigate } from "react-router-dom";
import "./index.css";

// Create a component for the search page
const SearchPage = ({ setBusData, setError }) => {
  const [source, setSource] = useState("");
  const [destination, setDestination] = useState("");
  const [journeyDate, setJourneyDate] = useState("");

  const navigate = useNavigate(); // Use navigate hook for routing after search

  // Function to handle the search
  const searchBuses = async (e) => {
    e.preventDefault();
    setError(null);
    setBusData(null);

    try {
      const response = await fetch(
        `http://127.0.0.1:5000/get_buses?source=${source}&destination=${destination}&jdate=${journeyDate}`
      );
      const data = await response.json();

      if (data.serviceDetailsList) {
        setBusData(data.serviceDetailsList);
        // Navigate to the results page after search
        navigate("/results");
      } else {
        setError("No buses found for the given criteria.");
      }
    } catch (err) {
      setError("Failed to fetch bus data. Please try again later.");
    }
  };

  return (
    <div className="search-page">
      <h1>Bus Ticket Search</h1>
      <form onSubmit={searchBuses}>
        <div>
          <label>From:</label>
          <input
            type="text"
            value={source}
            onChange={(e) => setSource(e.target.value)}
            placeholder="Enter source city"
          />
        </div>
        <div>
          <label>To:</label>
          <input
            type="text"
            value={destination}
            onChange={(e) => setDestination(e.target.value)}
            placeholder="Enter destination city"
          />
        </div>
        <div>
          <label>Journey Date:</label>
          <input
            type="date"
            value={journeyDate}
            onChange={(e) => setJourneyDate(e.target.value)}
          />
        </div>
        <button type="submit">Search Buses</button>
      </form>
    </div>
  );
};

// Create a component for the bus results page
const ResultsPage = ({ busData, error }) => {
  useEffect(() => {
    // Example of using jQuery in React - hiding the error message
    if (error) {
      $("#error-message").fadeIn();
    }
  }, [error]);

  return (
    <div className="results-page">
      <h1>Bus Search Results</h1>
      {error && <p id="error-message" className="error">{error}</p>}
      {busData && (
        <div className="bus-results">
          {busData.map((bus, index) => (
            <div key={index} className="bus-card">
              <div className="bus-info">
                <h3>{bus.travelerAgentName}</h3>
                <p><strong>Bus Type:</strong> {bus.busTypeName}</p>
                <p><strong>Start Time:</strong> {bus.startTime}</p>
                <p><strong>Arrival Time:</strong> {bus.arriveTime}</p>
                <p><strong>Travel Time:</strong> {bus.travelTime}</p>
                <p><strong>Seats Available:</strong> {bus.availableSeats}</p>
                <p className="fare">Fare: â‚¹{bus.fare}</p>
              </div>
              <button className="book-btn">Book Now</button>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

// Main App component with routing
const App = () => {
  const [busData, setBusData] = useState(null);
  const [error, setError] = useState(null);

  return (
    <Router>
      <Routes>
        {/* Route for the search page */}
        <Route path="/" element={<SearchPage setBusData={setBusData} setError={setError} />} />

        {/* Route for the results page */}
        <Route path="/results" element={<ResultsPage busData={busData} error={error} />} />
      </Routes>
    </Router>
  );
};

export default App;
