// src/App.js
import React, { useState } from 'react';
import './App.css';
import SearchForm from './components/SearchForm';
import BusResults from './components/BusResults';

const App = () => {
    const [buses, setBuses] = useState([]);

    const handleSearch = async (searchParams) => {
        const { source, destination, journeyDate } = searchParams;

        try {
            const response = await fetch(`/get_buses?source=${source}&destination=${destination}&jdate=${journeyDate}`);
            const data = await response.json();

            if (data.serviceDetailsList) {
                setBuses(data.serviceDetailsList);
            } else {
                setBuses([]);
            }
        } catch (error) {
            console.error('Error fetching bus data:', error);
        }
    };

    return (
        <div className="App">
            <h1>Bus Ticket Search</h1>
            <SearchForm onSearch={handleSearch} />
            <BusResults buses={buses} />
        </div>
    );
};

export default App;
